const masterFile="../json/artists.json",localJson="currentPage";async function readJson(fileUrl){try{const response=await fetch(fileUrl),jsonContent=await response.json();return jsonContent}catch(error){console.log(error)}}function storeData(tag,data){localStorage.setItem(tag,data)}function retrieveLocalData(tag){return localStorage.getItem(tag)}function storeDataAsJSON(tag,data){localStorage.setItem(tag,JSON.stringify(data))}function retrieveLocalDataAsJSON(tag,data){return data=localStorage.getItem(tag),JSON.parse(data)}